import { createStore, combineReducers } from 'redux';
import NavReducer from './NavReducer';
import SlideReducer from './SlideReducer';


let reducers = combineReducers({
	Nav: NavReducer,
	Slide: SlideReducer
});

let store = createStore(reducers);

window.store = store;
export default store;
