import React from 'react'
import classes from './Slide.module.css'
import NewActiveSlide from '../../redux/SlideReducer'
import { connect } from 'react-redux'


const Slide = (props) =>{
	/*<div className={classes.Line}></div>
			<div className={classes.Line}></div>
			<div className={classes.Line}></div>*/
	const SlideCreator = () => {
		if (props.activeSlide === 0){ return <div className={classes.Line}></div>}else{ return <div className={classes.Line}></div>}
		if (props.activeSlide === 1){ return <div className={classes.Line}></div>}else{ return <div className={classes.Line}></div>}
		if (props.activeSlide === 2){ return <div className={classes.Line}></div>}else{ return <div className={classes.Line}></div>}
	}
	return <section className={classes.section}>
		<div className={classes.Left}>

		</div>
		<div className={classes.Slide_number}>
			{ 
				 SlideCreator()
			}
		</div>
		<div className={classes.Right}> 
			<div className={classes.Slide}>
				<div className={classes.Text}>
					<h2>JP Funride 2014</h2>
					<div>Super easy going freeride boards based on the X-Cite Ride shape concept with additional control and super easy jibing. </div>
				</div>
				<div className={classes.Buttons}>
					<div className={classes.Button}>
						<div className={classes.arrow}>&#8249;</div>
					</div>
					<div className={classes.ButtonR}>
						<div className={classes.arrow}>&#8250;</div>
					</div>
				</div>
			</div>
			<div className={classes.Buy}><div  className={classes.BuyText}>BUY NOW</div></div>
		</div>
	</section>
}

const mapStateToProps = (state) => {
    return{
        activeSlide:state.Slide.activeSlide
    }
}


export default connect(mapStateToProps,{})(Slide)
