let initialState = { 
	style:'hidden',
	localUrl:window.location.pathname,
	Category:'',
	Brand:''
}

const NavReducer = (state = initialState , action) => {

	switch(action.type){
		case 'NEW-STYLE' :
		return{
			...state,	
			style : 'auto'
		}
		case 'CHANGE-URL' :
		if (action.key === 'Category') {
			return{
				...state,	
				Category : action.value
			}
		}else{
			return{
				...state,	
				Brand : action.value
			}
		}
		

		default:
			return state;	
	} 	
}

export const NewStyle = () => {
	return {
		type:'NEW-STYLE'
	}
}

export const ChangeUrl = (key,value) => {
	debugger
	return {
		type:'CHANGE-URL',
		key,
		value
	}
}

export default NavReducer;
