import React from 'react'
import classes from './CurrentElement.module.css'
import { connect } from 'react-redux'
import { NewActiveSlideProduct,ChangeLengthSurfApparel } from '../../redux/ProductReducer';
import {ChangeUrl} from '../../redux/NavReducer'
import {useParams} from "react-router-dom";

const CurrentElement = (props) =>{
	/*const handleChange = (e) =>{
		props.ChangeLengthSurfApparel(e.target.value)
	}*/

  let { slug } = useParams(),
  str = props.SurfProduct[Number(slug-1)]

  //img={str.img} Prise={str.Prise} Sale={str.Sale} text={str.text} id={str.id} 
  //QuickOverview={str.QuickOverview} Availability={str.Availability} Small_img={str.Small_img} Star={str.Star}
  //{img:`https://cdn-5ca51fc3f911c91ddc742258.closte.com/wp-content/uploads/2014/07/wetsuit.jpg`,text:'Surf Apparel',Prise:'â‚¬.36.00',Sale:'',id:1, 
  //  QuickOverview:`Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. When an unknown printer took a galley of type.`,
  // Availability:'In stock',Size:29,Length:32, Small_img:`https://images.ua.prom.st/1606817428_w640_h640_gidrokostyum-tribord-olaian.jpg` }
	return <section className={classes.section}>
    <div className={classes.Top}>
      <div className={classes.Left}>
        <div className={classes.Img}><img src={str.img} alt="" /></div>
        <div className={classes.Img_slider}>
          <div className={classes.Button}>
            <div className={classes.arrowL}></div>
          </div>
          <div className={classes.Small_Img}>
            <img src={str.Small_img} alt="" />
            <img src={str.Small_img} alt="" />
          </div>
          <div className={classes.Button}>
            <div className={classes.arrowR}></div>
          </div>
        </div>
      </div>
      <div className={classes.Right}>
        <div>
          <h2>{str.text}</h2>
          <div className={classes.stars}>
            <div>{str.Star.map((el) => {
                if(el === 1){
                  return <div>&#9733;</div>
                }else{
                  return <div className={classes.GreyStar}>&#9733;</div>
                }
              })}
            </div>
            <div className={classes.Review}> &nbsp;1 Review(s) | Add Your Review</div>
          </div>
          <div className={classes.description}>
            <div className={classes.Prise}>{str.Prise}</div>
            <div>
              <div className={classes.Greytxt}>Product code: {str.id}</div>
              <div className={classes.Greytxt}>Availability: {str.Availability}</div>
            </div>
          </div>
        </div>
        <div></div>
        <div></div>
        <div></div>
      </div>
    </div>
    <div className={classes.Middle}></div>
    <div className={classes.Slider}></div>
    <div className={classes.Slider}></div>
    <div></div>
    <div></div>
    <div></div>
    <div></div>

    
	</section>
}

const mapStateToProps = (state) => {
    return{
    	ActivePage:state.Products.ActivePageFromSurfApparel,
    	SurfProduct:state.Products.SurfProduct,
    	LengthSurfApparel:state.Products.LengthSurfApparel,
    }
}


export default connect(mapStateToProps,{NewActiveSlideProduct,ChangeLengthSurfApparel,ChangeUrl})(CurrentElement)
