const ProductReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'NEW-STYLE':
      return { ...state,
        style: 'auto'
      };

    case 'NEW-ACTIVE-SLIDE-PRODUCT':
      if (action.text === 'New Products') {
        if (action.ActivePageNewProducts < 1) {
          action.ActivePageNewProducts = 1;
        }

        if (action.ActivePageNewProducts > Math.ceil(state.NewProducts.length / 6)) {
          console.log(Math.ceil(state.NewProducts.length / 6));
          action.ActivePageNewProducts = 1;
        }

        return { ...state,
          ActivePageNewProducts: action.ActivePageNewProducts
        };
      }

      if (action.text === 'Top Products') {
        if (action.ActivePageNewProducts < 1) {
          action.ActivePageNewProducts = 1;
        }

        if (action.ActivePageNewProducts > Math.ceil(state.NewProducts.length / 3)) {
          action.ActivePageNewProducts = 1;
        }

        return { ...state,
          ActivePageTopProducts: action.ActivePageNewProducts
        };
      }

      if (action.text === 'Sale Products') {
        if (action.ActivePageNewProducts < 1) {
          action.ActivePageNewProducts = 1;
        }

        if (action.ActivePageNewProducts > Math.ceil(state.NewProducts.length / 3)) {
          action.ActivePageNewProducts = 1;
        }

        return { ...state,
          ActivePageSaleProducts: action.ActivePageNewProducts
        };
      }

      if (action.text === 'SurfApparel') {
        if (action.ActivePageNewProducts < 1) {
          action.ActivePageNewProducts = 1;
        }

        if (action.ActivePageNewProducts > Math.ceil(state.SurfProduct.length / state.LengthSurfApparel)) {
          action.ActivePageNewProducts = 1;
        }

        return { ...state,
          ActivePageFromSurfApparel: action.ActivePageNewProducts
        };
      }

    case 'CHANGE-LENGTH-SURF-APPAREL':
      return { ...state,
        LengthSurfApparel: action.Length
      };

    default:
      return state;
  }
};

export const NewStyle = () => {
  return {
    type: 'NEW-STYLE'
  };
};
export const NewActiveSlideProduct = (ActivePageNewProducts, text) => {
  return {
    type: 'NEW-ACTIVE-SLIDE-PRODUCT',
    ActivePageNewProducts,
    text
  };
};
export const ChangeLengthSurfApparel = Length => {
  return {
    type: 'CHANGE-LENGTH-SURF-APPAREL',
    Length
  };
};
export default ProductReducer;

