let initialState = { 
	style:'hidden'
}

const NavReducer = (state = initialState , action) => {

	switch(action.type){
		case 'NEW-STYLE' :
		return{
			...state,	
			style : 'auto'
		}

		default:
			return state;	
	} 	
}

export const newMessageActionCreator = () => {
	return {
		type:'NEW-STYLE'
	}
}



export default NavReducer;
