let initialState = { 
	Product:[]
}

const CartReducer = (state = initialState , action) => {

	switch(action.type){
		case 'ADD-PRODUCT-TO-CART' :
		return{
			...state,	
			Product : [...state.arr, action.newItem]
		}
		
		

		default:
			return state;	
	} 	
}

export const AddProductToCart = (product) => {
	return {
		type:'ADD-PRODUCT-TO-CART'
	}
}




export default CartReducer;
