let initialState = { 
	NewMessageText:''
}

const NavReducer = (state = initialState , action) => {

	switch(action.type){
		case 'NEW-MESSAGE' :
			let addMessage = {id:4,name:'Elon', sms: state.NewMessageText}
		return{
			...state,	
			Message : [...state.Message , addMessage],
			NewMessageText : ''
		}

		default:
			return state;	
	} 	
}

export const newMessageActionCreator = () => {
	return {
		type:'NEW-MESSAGE'
	}
}



export default NavReducer;
